package org.decisionmaking;

public class decisionmakingex2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=15,b=5,c=16;
		if(a>b && a>c) 
		{
		    System.out.println(a);	
		}
		else if(b>a && b>c)
		{
			System.out.println(b);
		}
		else
		{
			System.out.println(c);
		}
	}

}
